func:
// === 향상된 스마트 교통 제어 로직 (긴급차량 처리 수정) ===

// --- 입력 데이터 읽기 ---
const MODE = msg.payload[0] || 1;
const EVP_SIGNAL = msg.payload[20] || 0;
const EVP_DIRECTION = msg.payload[21] || 0; // 1=NS, 2=EW

// SCADA로부터 받은 적응형 제어 파라미터
const GREEN_TIME_NS_BASE = msg.payload[10] || 10;
const GREEN_TIME_EW_BASE = msg.payload[11] || 10;
const VOLUME_NS = msg.payload[12] || 0;
const VOLUME_EW = msg.payload[13] || 0;

// 추가 파라미터
const HOUR_OF_DAY = msg.payload[14] || new Date().getHours();
const PEDESTRIAN_REQUEST_NS = msg.payload[15] || 0;
const PEDESTRIAN_REQUEST_EW = msg.payload[16] || 0;
const CONGESTION_LEVEL = msg.payload[17] || 0; // 0=낮음, 1=중간, 2=높음
const ADJACENT_SYNC = msg.payload[18] || 0; // 인접 교차로 동기화 신호

// --- 상수 정의 ---
const YELLOW_TIME = 3;
const PEDESTRIAN_TIME = 7;
const MIN_GREEN = 5;
const MAX_GREEN = 30;
const HIGH_TRAFFIC_THRESHOLD = 70;
const CRITICAL_TRAFFIC_THRESHOLD = 85;

// --- 적응형 녹색 시간 계산 ---
function calculateAdaptiveGreenTime(baseTime, volume, isPeak) {
    let adaptiveTime = baseTime;

    // 교통량 기반 조정
    if (volume > CRITICAL_TRAFFIC_THRESHOLD) {
        adaptiveTime = Math.min(baseTime * 1.5, MAX_GREEN);
    } else if (volume > HIGH_TRAFFIC_THRESHOLD) {
        adaptiveTime = Math.min(baseTime * 1.3, MAX_GREEN);
    }

    // 시간대별 가중치 적용
    if (isPeak) {
        adaptiveTime = Math.min(adaptiveTime * 1.2, MAX_GREEN);
    }

    // 혼잡도 기반 추가 조정
    if (CONGESTION_LEVEL === 2) {
        adaptiveTime = Math.min(adaptiveTime + 5, MAX_GREEN);
    } else if (CONGESTION_LEVEL === 1) {
        adaptiveTime = Math.min(adaptiveTime + 2, MAX_GREEN);
    }

    return Math.max(Math.round(adaptiveTime), MIN_GREEN);
}

// 피크 시간대 판단 (7-9시, 18-20시)
const isPeakHour = (HOUR_OF_DAY >= 7 && HOUR_OF_DAY <= 9) ||
    (HOUR_OF_DAY >= 18 && HOUR_OF_DAY <= 20);

// 최종 녹색 시간 계산
const GREEN_TIME_NS = calculateAdaptiveGreenTime(GREEN_TIME_NS_BASE, VOLUME_NS, isPeakHour);
const GREEN_TIME_EW = calculateAdaptiveGreenTime(GREEN_TIME_EW_BASE, VOLUME_EW, isPeakHour);

// --- 상태 관리 ---
let state = context.get('state') || 0;
let timer = context.get('timer') || 0;
let cycleCount = context.get('cycleCount') || 0;

// 신호등 배열 초기화 (12개 램프 + 8개 보행자 신호)
let lights = new Array(20).fill(false);

// ==================== 신호 로직 시작 ====================

// --- 긴급차량 우선 신호 처리 (수정된 로직) ---
if (EVP_SIGNAL > 0) {
    // 긴급 신호가 있으면, 즉시 신호등을 설정하고 다른 모든 로직을 건너뜁니다.
    if (EVP_DIRECTION === 1) { // 남북(NS) 방향 긴급 신호
        lights[2] = true;  // 남쪽 녹색
        lights[8] = true;  // 북쪽 녹색
        lights[3] = true;  // 동쪽 적색
        lights[9] = true;  // 서쪽 적색
    } else if (EVP_DIRECTION === 2) { // 동서(EW) 방향 긴급 신호
        lights[5] = true;  // 동쪽 녹색
        lights[11] = true; // 서쪽 녹색
        lights[0] = true;  // 남쪽 적색
        lights[6] = true;  // 북쪽 적색
    }
    // 긴급 모드였다는 것을 기록하여, 해제 후 주기를 초기화하도록 합니다.
    context.set('wasInEVP', true);

} else {
    // --- 일반 신호 주기 ---
    const wasInEVP = context.get('wasInEVP') || false;

    // 긴급 모드가 방금 해제되었다면, 안전을 위해 주기를 처음부터 다시 시작합니다.
    if (wasInEVP) {
        timer = 0;
        state = 0;
        context.set('wasInEVP', false); // 플래그 초기화
    }

    switch (state) {
        case 0: // NS 녹색
            lights[2] = true; lights[8] = true; // NS 녹색
            lights[3] = true; lights[9] = true; // EW 적색

            if (PEDESTRIAN_REQUEST_NS && timer < PEDESTRIAN_TIME) {
                lights[12] = true; lights[13] = true;
            }

            if (timer >= GREEN_TIME_NS) {
                state++;
                timer = 0;
            }
            break;

        case 1: // NS 황색
            lights[1] = true; lights[7] = true; // NS 황색
            lights[3] = true; lights[9] = true; // EW 적색
            if (timer >= YELLOW_TIME) {
                state++;
                timer = 0;
            }
            break;

        case 2: // 전체 적색 (안전 구간)
            lights[0] = true; lights[6] = true; // NS 적색
            lights[3] = true; lights[9] = true; // EW 적색
            if (timer >= 1) {
                state++;
                timer = 0;
            }
            break;

        case 3: // EW 녹색
            lights[5] = true; lights[11] = true; // EW 녹색
            lights[0] = true; lights[6] = true;  // NS 적색

            if (PEDESTRIAN_REQUEST_EW && timer < PEDESTRIAN_TIME) {
                lights[14] = true; lights[15] = true;
            }

            if (timer >= GREEN_TIME_EW) {
                state++;
                timer = 0;
            }
            break;

        case 4: // EW 황색
            lights[4] = true; lights[10] = true; // EW 황색
            lights[0] = true; lights[6] = true;  // NS 적색
            if (timer >= YELLOW_TIME) {
                state++;
                timer = 0;
            }
            break;

        case 5: // 전체 적색 (안전 구간)
            lights[0] = true; lights[6] = true; // NS 적색
            lights[3] = true; lights[9] = true; // EW 적색
            if (timer >= 1) {
                state = 0;
                timer = 0;
                cycleCount++;
            }
            break;
    }

    timer++;
}

// ==================== 신호 로직 종료 ====================

// --- 상태 저장 ---
context.set('state', state);
context.set('timer', timer);
context.set('cycleCount', cycleCount);

// --- 진단 정보 생성 ---
const diagnostics = {
    state: state,
    timer: timer,
    cycleCount: cycleCount,
    greenTimeNS: GREEN_TIME_NS,
    greenTimeEW: GREEN_TIME_EW,
    volumeNS: VOLUME_NS,
    volumeEW: VOLUME_EW,
    congestionLevel: CONGESTION_LEVEL,
    isPeakHour: isPeakHour,
    isEVP: EVP_SIGNAL > 0 ? `Active-${EVP_DIRECTION === 1 ? 'NS' : 'EW'}` : 'Inactive'
};

// --- 출력 메시지 생성 ---
msg.payload = {
    'value': lights,
    'fc': 15,
    'unitid': 1,
    'address': 0,
    'quantity': 20
};

msg.diagnostics = diagnostics;

return msg;

let blink = context.get('blink') || false;
blink = !blink;
context.set('blink', blink);

let lights = new Array(20).fill(false);

if (blink) {
    lights[1] = true;  // South Yellow
    lights[4] = true;  // East Yellow
    lights[7] = true;  // North Yellow
    lights[10] = true; // West Yellow
}

msg.payload = {
    'value': lights,
    'fc': 15,
    'unitid': 1,
    'address': 0,
    'quantity': 20
};

return msg;

let lights = new Array(20).fill(false);

lights[0] = true; // South Red
lights[3] = true; // East Red
lights[6] = true; // North Red
lights[9] = true; // West Red

msg.payload = {
    'value': lights,
    'fc': 15,
    'unitid': 1,
    'address': 0,
    'quantity': 20
};

return msg;

// 진단 정보를 Holding Register에 저장
const diag = msg.diagnostics;

if (diag) {
    const diagData = [
        diag.state,
        diag.timer,
        diag.cycleCount,
        diag.greenTimeNS,
        diag.greenTimeEW,
        diag.volumeNS,
        diag.volumeEW,
        diag.congestionLevel,
        diag.isPeakHour ? 1 : 0
    ];

    msg.payload = {
        'value': diagData,
        'fc': 16,
        'unitid': 1,
        'address': 100,
        'quantity': 9
    };

    return msg;
}

return null;

// 보행자 버튼 입력 처리
const buttonNS = msg.payload.buttonNS || false;
const buttonEW = msg.payload.buttonEW || false;

let requests = flow.get('pedestrianRequests') || {NS: 0, EW: 0};

if (buttonNS) {
    requests.NS = 1;
}
if (buttonEW) {
    requests.EW = 1;
}

// 신호가 바뀌면 요청 리셋
if (msg.reset) {
    if (msg.reset === 'NS') requests.NS = 0;
    if (msg.reset === 'EW') requests.EW = 0;
}

flow.set('pedestrianRequests', requests);

// Holding Register에 쓰기
msg.payload = {
    'value': [requests.NS, requests.EW],
    'fc': 16,
    'unitid': 1,
    'address': 15,
    'quantity': 2
};

return msg;
