const trafficVolumes = [];
for (let i = 0; i < 4; i++) {
    const volumeNS = Math.floor(Math.random() * 100);
    const volumeEW = Math.floor(Math.random() * 100);
    trafficVolumes.push(volumeNS, volumeEW);
}
msg.payload = { 
    value: trafficVolumes, 
    'fc': 16, 
    'unitid': 1, 
    'address': 100, 
    'quantity': 8 
};
return msg;

const volumes = msg.payload;
const intersections = ['a', 'b', 'c', 'd'];

// 데이터가 유효하지 않으면 로직을 실행하지 않음
if (!Array.isArray(volumes) || volumes.length < 8) {
    return null;
}

function calculateGreenTime(volume) {
    return 5 + Math.round((volume / 100) * 10);
}
for (let i = 0; i < intersections.length; i++) {
    const ns_green_time = calculateGreenTime(volumes[i * 2]);
    const ew_green_time = calculateGreenTime(volumes[i * 2 + 1]);
    node.send({
        topic: intersections[i],
        payload: { 
            value: [ns_green_time, ew_green_time],
            'fc': 16, 
            'unitid': 1, 
            'address': 10, 
            'quantity': 2
        }
    });
}
return null;

const topics = ['A_NS', 'A_EW', 'B_NS', 'B_EW', 'C_NS', 'C_EW', 'D_NS', 'D_EW'];
const outputs = [];

// 데이터가 배열이 아니거나 길이가 맞지 않으면 (초기화, 에러 상황)
if (!Array.isArray(msg.payload) || msg.payload.length < 8) {
    // 모든 게이지에 안전한 기본값 0을 보냄
    for (let i = 0; i < 8; i++) {
        outputs.push({
            topic: topics[i],
            payload: 0
        });
    }
    node.warn("Invalid Modbus data. Sending default 0 to gauges.");
    return outputs;
}

// 정상 데이터 처리
for (let i = 0; i < 8; i++) {
    outputs.push({
        topic: topics[i],
        payload: msg.payload[i]
    });
}

return outputs;

