func:
// === 향상된 스마트 교통 제어 로직 (Red->Green 전환 보장) ===

// --- 입력 데이터 읽기 ---
const EVP_SIGNAL = msg.payload[20] || 0;
const EVP_DIRECTION = msg.payload[21] || 0; // 1=NS, 2=EW
const GREEN_TIME_NS_BASE = msg.payload[10] || 10;
const GREEN_TIME_EW_BASE = msg.payload[11] || 10;
const VOLUME_NS = msg.payload[12] || 0;
const VOLUME_EW = msg.payload[13] || 0;
const HOUR_OF_DAY = msg.payload[14] || new Date().getHours();
const PEDESTRIAN_REQUEST_NS = msg.payload[15] || 0;
const PEDESTRIAN_REQUEST_EW = msg.payload[16] || 0;
const CONGESTION_LEVEL = msg.payload[17] || 0;

// --- 상수 정의 ---
const YELLOW_TIME = 3;
const ALL_RED_TIME = 2;
const PEDESTRIAN_TIME = 7;
const MIN_GREEN = 5;
const MAX_GREEN = 30;

// --- 적응형 녹색 시간 계산 함수 ---
function calculateAdaptiveGreenTime(baseTime, volume, isPeak) {
    let adaptiveTime = baseTime;
    const HIGH_TRAFFIC_THRESHOLD = 70;
    const CRITICAL_TRAFFIC_THRESHOLD = 85;
    if (volume > CRITICAL_TRAFFIC_THRESHOLD) {
        adaptiveTime = Math.min(baseTime * 1.5, MAX_GREEN);
    } else if (volume > HIGH_TRAFFIC_THRESHOLD) {
        adaptiveTime = Math.min(baseTime * 1.3, MAX_GREEN);
    }
    if (isPeak) {
        adaptiveTime = Math.min(adaptiveTime * 1.2, MAX_GREEN);
    }
    if (CONGESTION_LEVEL === 2) {
        adaptiveTime = Math.min(adaptiveTime + 5, MAX_GREEN);
    } else if (CONGESTION_LEVEL === 1) {
        adaptiveTime = Math.min(adaptiveTime + 2, MAX_GREEN);
    }
    return Math.max(Math.round(adaptiveTime), MIN_GREEN);
}

// --- 전역 변수 및 상태 관리 ---
const isPeakHour = (HOUR_OF_DAY >= 7 && HOUR_OF_DAY <= 9) || (HOUR_OF_DAY >= 18 && HOUR_OF_DAY <= 20);
const GREEN_TIME_NS = calculateAdaptiveGreenTime(GREEN_TIME_NS_BASE, VOLUME_NS, isPeakHour);
const GREEN_TIME_EW = calculateAdaptiveGreenTime(GREEN_TIME_EW_BASE, VOLUME_EW, isPeakHour);
let state = context.get('state') || 0;
let timer = context.get('timer') || 0;
let cycleCount = context.get('cycleCount') || 0;
let lights = new Array(20).fill(false);

// ==================== 신호 로직 시작 ====================

if (EVP_SIGNAL > 0) {
    // (긴급차량 로직은 변경 없음)
    let wasInEVP = context.get('wasInEVP') || false;
    let lastEVPDirection = context.get('lastEVPDirection') || 0;
    let evpTimer = context.get('evpTimer') || 0;
    let isClearing = context.get('isClearing') || false;

    if (!wasInEVP || (EVP_DIRECTION !== lastEVPDirection)) {
        const directionToClear = wasInEVP ? lastEVPDirection : (EVP_DIRECTION === 1 ? 2 : 1);
        const greenStateToCheck = directionToClear === 1 ? 0 : 3;
        if (state === greenStateToCheck || wasInEVP) {
            isClearing = true;
            evpTimer = 0;
        }
    }

    if (isClearing && evpTimer < YELLOW_TIME) {
        const directionToClear = lastEVPDirection || (EVP_DIRECTION === 1 ? 2 : 1);
        if (directionToClear === 1) { lights[1] = true; lights[7] = true; lights[3] = true; lights[9] = true; }
        else { lights[4] = true; lights[10] = true; lights[0] = true; lights[6] = true; }
        evpTimer++;
    } else {
        isClearing = false;
        if (EVP_DIRECTION === 1) { lights[2] = true; lights[8] = true; lights[3] = true; lights[9] = true; }
        else { lights[5] = true; lights[11] = true; lights[0] = true; lights[6] = true; }
    }

    context.set('wasInEVP', true);
    context.set('lastEVPDirection', EVP_DIRECTION);
    context.set('isClearing', isClearing);
    context.set('evpTimer', evpTimer);

} else {
    // --- 일반 신호 주기 ---
    const wasInEVP = context.get('wasInEVP') || false;
    if (wasInEVP) {
        state = 0; timer = 0;
        context.set('wasInEVP', false); context.set('evpState', null); context.set('evpTimer', 0);
    }

    switch (state) {
        case 0: // NS 녹색
            lights[2] = true; lights[8] = true; lights[3] = true; lights[9] = true;
            if (PEDESTRIAN_REQUEST_NS && timer < PEDESTRIAN_TIME) { lights[12] = true; lights[13] = true; }
            if (timer >= GREEN_TIME_NS) { state = 1; timer = 0; }
            break;
        case 1: // NS 황색
            lights[1] = true; lights[7] = true; lights[3] = true; lights[9] = true;
            if (timer >= YELLOW_TIME) { state = 3; timer = 0; }
            break;
        case 3: // EW 녹색
            lights[5] = true; lights[11] = true; lights[0] = true; lights[6] = true;
            if (PEDESTRIAN_REQUEST_EW && timer < PEDESTRIAN_TIME) { lights[14] = true; lights[15] = true; }
            if (timer >= GREEN_TIME_EW) { state = 4; timer = 0; }
            break;
        case 4: // EW 황색
            lights[4] = true; lights[10] = true; lights[0] = true; lights[6] = true;
            if (timer >= YELLOW_TIME) { state = 0; timer = 0; cycleCount++; }
            break;
    }
    timer++;
}

// --- 최종 상태 저장 및 출력 ---
context.set('state', state);
context.set('timer', timer);
context.set('cycleCount', cycleCount);
const diagnostics = { state: state, timer: timer, isEVP: EVP_SIGNAL > 0 ? `Active-${EVP_DIRECTION === 1 ? 'NS' : 'EW'}` : 'Inactive' };
msg.payload = { 'value': lights, 'fc': 15, 'unitid': 1, 'address': 0, 'quantity': 20 };
msg.diagnostics = diagnostics;
return msg;

let blink = context.get('blink') || false;
blink = !blink;
context.set('blink', blink);

let lights = new Array(20).fill(false);

if (blink) {
    lights[1] = true;  // South Yellow
    lights[4] = true;  // East Yellow
    lights[7] = true;  // North Yellow
    lights[10] = true; // West Yellow
}

msg.payload = {
    'value': lights,
    'fc': 15,
    'unitid': 1,
    'address': 0,
    'quantity': 20
};

return msg;

let lights = new Array(20).fill(false);

lights[0] = true; // South Red
lights[3] = true; // East Red
lights[6] = true; // North Red
lights[9] = true; // West Red

msg.payload = {
    'value': lights,
    'fc': 15,
    'unitid': 1,
    'address': 0,
    'quantity': 20
};

return msg;

// 진단 정보를 Holding Register에 저장
const diag = msg.diagnostics;

if (diag) {
    const diagData = [
        diag.state,
        diag.timer,
        diag.cycleCount,
        diag.greenTimeNS,
        diag.greenTimeEW,
        diag.volumeNS,
        diag.volumeEW,
        diag.congestionLevel,
        diag.isPeakHour ? 1 : 0
    ];

    msg.payload = {
        'value': diagData,
        'fc': 16,
        'unitid': 1,
        'address': 100,
        'quantity': 9
    };

    return msg;
}

return null;

// 보행자 버튼 입력 처리
const buttonNS = msg.payload.buttonNS || false;
const buttonEW = msg.payload.buttonEW || false;

let requests = flow.get('pedestrianRequests') || {NS: 0, EW: 0};

if (buttonNS) {
    requests.NS = 1;
}
if (buttonEW) {
    requests.EW = 1;
}

// 신호가 바뀌면 요청 리셋
if (msg.reset) {
    if (msg.reset === 'NS') requests.NS = 0;
    if (msg.reset === 'EW') requests.EW = 0;
}

flow.set('pedestrianRequests', requests);

// Holding Register에 쓰기
msg.payload = {
    'value': [requests.NS, requests.EW],
    'fc': 16,
    'unitid': 1,
    'address': 15,
    'quantity': 2
};

return msg;
