func:
// msg.payload[0] = Mode
// msg.payload[10] = NS Green Time (from TMC)
// msg.payload[11] = EW Green Time (from TMC)

const GREEN_TIME_NS = msg.payload[10] || 5;
const GREEN_TIME_EW = msg.payload[11] || 5;
const VOLUME_NS = msg.payload[12] || 0;
const VOLUME_EW = msg.payload[13] || 0;

const YELLOW_TIME = 2;
const HIGH_TRAFFIC_THRESHOLD = 75;

const MAX_GREEN_TIME_NS = GREEN_TIME_NS + 6;
const MAX_GREEN_TIME_EW = GREEN_TIME_EW + 6;

// ✅ 추가됨: 양방향 동시 혼잡 상태를 확인하는 변수
const isBothCongested = (VOLUME_NS > HIGH_TRAFFIC_THRESHOLD && VOLUME_EW > HIGH_TRAFFIC_THRESHOLD);

let state = context.get('state') || 0;
let timer = context.get('timer') || 0;

let lights = new Array(12).fill(false);

// State machine for traffic light sequence
switch (state) {
    // --- South-North Green ---
    case 0:
        lights[2] = true;
        lights[8] = true;
        lights[3] = true;
        lights[9] = true;

        if (timer >= GREEN_TIME_NS) {
            // ✅ 수정됨: '양방향 혼잡'이 아닐 때만 신호 연장을 허용
            if (!isBothCongested && VOLUME_NS > HIGH_TRAFFIC_THRESHOLD && timer < MAX_GREEN_TIME_NS) {
                timer -= 2;
            } else {
                state++;
                timer = 0;
            }
        }
        break;

    case 1:
        lights[1] = true;
        lights[7] = true;
        lights[3] = true;
        lights[9] = true;
        if (timer >= YELLOW_TIME) { state++; timer = 0; }
        break;

    // --- East-West Green ---
    case 2:
        lights[5] = true;
        lights[11] = true;
        lights[0] = true;
        lights[6] = true;

        if (timer >= GREEN_TIME_EW) {
            // ✅ 수정됨: '양방향 혼잡'이 아닐 때만 신호 연장을 허용
            if (!isBothCongested && VOLUME_EW > HIGH_TRAFFIC_THRESHOLD && timer < MAX_GREEN_TIME_EW) {
                timer -= 2;
            } else {
                state++;
                timer = 0;
            }
        }
        break;

    case 3:
        lights[4] = true;
        lights[10] = true;
        lights[0] = true;
        lights[6] = true;
        if (timer >= YELLOW_TIME) { state = 0; timer = 0; }
        break;
}

timer++;
context.set('state', state);
context.set('timer', timer);

msg.payload = {
    'value': lights,
    'fc': 15,
    'unitid': 1,
    'address': 0,
    'quantity': 12
};

return msg;

let blink = context.get('blink') || false;
blink = !blink;
context.set('blink', blink);

let lights = new Array(12).fill(false);

if (blink) {
    lights[1] = true;  // South Yellow
    lights[4] = true;  // East Yellow
    lights[7] = true;  // North Yellow
    lights[10] = true; // West Yellow
}

msg.payload = {
    'value': lights,
    'fc': 15,
    'unitid': 1,
    'address': 0,
    'quantity': 12
};

return msg;

let lights = new Array(12).fill(false);

lights[0] = true; // South Red
lights[3] = true; // East Red
lights[6] = true; // North Red
lights[9] = true; // West Red

msg.payload = {
    'value': lights,
    'fc': 15,
    'unitid': 1,
    'address': 0,
    'quantity': 12
};

return msg;
