// === ì§€ëŠ¥í˜• íŠ¸ë˜í”½ ì‹œë®¬ë ˆì´í„° ===

const currentTime = new Date();
const hour = currentTime.getHours();
const minute = currentTime.getMinutes();
const dayOfWeek = currentTime.getDay();

// ì‹œê°„ëŒ€ë³„ íŠ¸ë˜í”½ íŒ¨í„´ ì •ì˜
function getTrafficPattern(hour, isWeekend) {
    if (isWeekend) {
        // ì£¼ë§ íŒ¨í„´
        if (hour >= 11 && hour <= 15) return { base: 60, variance: 20 };
        if (hour >= 18 && hour <= 21) return { base: 50, variance: 15 };
        return { base: 30, variance: 10 };
    } else {
        // í‰ì¼ íŒ¨í„´
        if (hour >= 7 && hour <= 9) return { base: 85, variance: 10 };   // ì•„ì¹¨ ëŸ¬ì‹œ
        if (hour >= 18 && hour <= 20) return { base: 90, variance: 10 }; // ì €ë… ëŸ¬ì‹œ
        if (hour >= 12 && hour <= 13) return { base: 60, variance: 15 }; // ì ì‹¬ì‹œê°„
        if (hour >= 22 || hour <= 5) return { base: 15, variance: 5 };   // ì‹¬ì•¼
        return { base: 40, variance: 15 };
    }
}

// êµì°¨ë¡œë³„ íŠ¸ë˜í”½ íŠ¹ì„± (ìœ„ì¹˜ë³„ ê°€ì¤‘ì¹˜)
const intersectionWeights = [
    { ns: 1.0, ew: 0.9 },  // êµì°¨ë¡œ A - ì£¼ë„ë¡œ/ë¶€ë„ë¡œ
    { ns: 0.8, ew: 1.1 },  // êµì°¨ë¡œ B - ë¶€ë„ë¡œ/ìƒì—…ì§€êµ¬
    { ns: 1.2, ew: 0.7 },  // êµì°¨ë¡œ C - ëŒ€ë¡œ/ê³¨ëª©ê¸¸
    { ns: 0.9, ew: 0.9 }   // êµì°¨ë¡œ D - ê· ë“±
];

const isWeekend = dayOfWeek === 0 || dayOfWeek === 6;
const pattern = getTrafficPattern(hour, isWeekend);

// ê° êµì°¨ë¡œë³„ íŠ¸ë˜í”½ ë³¼ë¥¨ ìƒì„±
const trafficVolumes = [];
for (let i = 0; i < 4; i++) {
    const weight = intersectionWeights[i];

    // ê¸°ë³¸ íŠ¸ë˜í”½ì— ë³€ë™ì„± ì¶”ê°€
    let volumeNS = pattern.base * weight.ns;
    let volumeEW = pattern.base * weight.ew;

    // ëœë¤ ë³€ë™ ì¶”ê°€ (Â±variance)
    volumeNS += (Math.random() - 0.5) * pattern.variance * 2;
    volumeEW += (Math.random() - 0.5) * pattern.variance * 2;

    // íŠ¹ë³„ ì´ë²¤íŠ¸ ì‹œë®¬ë ˆì´ì…˜ (10% í™•ë¥ ë¡œ í•œ ë°©í–¥ í˜¼ì¡)
    if (Math.random() < 0.1) {
        if (Math.random() < 0.5) {
            volumeNS = Math.min(volumeNS * 1.5, 100);
        } else {
            volumeEW = Math.min(volumeEW * 1.5, 100);
        }
    }

    // ë²”ìœ„ ì œí•œ (0-100)
    volumeNS = Math.max(0, Math.min(100, Math.round(volumeNS)));
    volumeEW = Math.max(0, Math.min(100, Math.round(volumeEW)));

    trafficVolumes.push(volumeNS, volumeEW);
}

// TMC í—ˆë¸Œì— ë°ì´í„° ì“°ê¸°
msg.payload = { 
    value: trafficVolumes, 
    fc: 16, 
    unitid: 1, 
    address: 100, 
    quantity: 8 
};

// ì§„ë‹¨ ì •ë³´ ì¶”ê°€
msg.diagnostics = {
    timestamp: currentTime.toISOString(),
    hour: hour,
    isWeekend: isWeekend,
    pattern: pattern.base,
    volumes: trafficVolumes
};

return msg;

// === êµí†µëŸ‰ ì˜ˆì¸¡ ì—”ì§„ ===

// ì´ë ¥ ë°ì´í„° ê´€ë¦¬
let history = flow.get('trafficHistory') || [];
const MAX_HISTORY = 60; // ìµœê·¼ 5ë¶„ ë°ì´í„° ìœ ì§€ (5ì´ˆë§ˆë‹¤ ìƒ˜í”Œë§)

// í˜„ì¬ ë°ì´í„° ì¶”ê°€
if (msg.diagnostics && msg.diagnostics.volumes) {
    history.push({
        timestamp: Date.now(),
        volumes: msg.diagnostics.volumes,
        hour: msg.diagnostics.hour
    });

    // ìµœëŒ€ í¬ê¸° ìœ ì§€
    if (history.length > MAX_HISTORY) {
        history = history.slice(-MAX_HISTORY);
    }

    flow.set('trafficHistory', history);
}

// ì˜ˆì¸¡ ìˆ˜í–‰
if (history.length >= 12) { // ìµœì†Œ 1ë¶„ ë°ì´í„° í•„ìš”
    const predictions = [];

    for (let i = 0; i < 8; i++) {
        // ê° ì°¨ì„ ë³„ ì´ë™ í‰ê·  ê³„ì‚°
        const recentValues = history.slice(-12).map(h => h.volumes[i]);
        const average = recentValues.reduce((a, b) => a + b, 0) / recentValues.length;

        // ì¶”ì„¸ ê³„ì‚°
        const oldAvg = recentValues.slice(0, 6).reduce((a, b) => a + b, 0) / 6;
        const newAvg = recentValues.slice(6, 12).reduce((a, b) => a + b, 0) / 6;
        const trend = newAvg - oldAvg;

        // ì˜ˆì¸¡ê°’ = í‰ê·  + ì¶”ì„¸
        let prediction = average + trend * 0.5;
        prediction = Math.max(0, Math.min(100, Math.round(prediction)));

        predictions.push(prediction);
    }

    // ì˜ˆì¸¡ ë°ì´í„°ë¥¼ ë³„ë„ ì£¼ì†Œì— ì €ì¥
    msg.payload = {
        value: predictions,
        fc: 16,
        unitid: 1,
        address: 110, // ì˜ˆì¸¡ ë°ì´í„° ì£¼ì†Œ
        quantity: 8
    };

    msg.predictions = predictions;
    return msg;
}

return null;

// === í–¥ìƒëœ ì ì‘í˜• ì œì–´ ë¡œì§ ===

const volumes = msg.payload.slice(0, 8);      // í˜„ì¬ íŠ¸ë˜í”½
const predictions = msg.payload.slice(8, 16); // ì˜ˆì¸¡ íŠ¸ë˜í”½ (ìˆìœ¼ë©´)
const intersections = ['a', 'b', 'c', 'd'];

if (!Array.isArray(volumes) || volumes.length < 8) {
    node.warn("ìœ íš¨í•˜ì§€ ì•Šì€ íŠ¸ë˜í”½ ë°ì´í„°");
    return null;
}

const currentHour = new Date().getHours();
const isPeakHour = (currentHour >= 7 && currentHour <= 9) || 
                   (currentHour >= 18 && currentHour <= 20);

// êµì°¨ë¡œ ê°„ ë™ê¸°í™”ë¥¼ ìœ„í•œ ì˜¤í”„ì…‹ ê³„ì‚°
function calculateSyncOffset(index) {
    // ì¸ì ‘ êµì°¨ë¡œì™€ ë…¹ìƒ‰ ì‹œì‘ ì‹œê°„ì„ ì¡°ì •í•˜ì—¬ ë…¹ìƒ‰íŒŒ ìƒì„±
    const baseOffset = index * 15; // 15ì´ˆì”© ì°¨ì´
    return baseOffset;
}

// í˜¼ì¡ë„ ê³„ì‚°
function calculateCongestionLevel(volumeNS, volumeEW) {
    const maxVolume = Math.max(volumeNS, volumeEW);
    if (maxVolume > 85) return 2; // ë†’ìŒ
    if (maxVolume > 60) return 1; // ì¤‘ê°„
    return 0; // ë‚®ìŒ
}

// ì ì‘í˜• ë…¹ìƒ‰ ì‹œê°„ ê³„ì‚°
function calculateOptimalGreenTime(currentVolume, predictedVolume, isPeak) {
    // í˜„ì¬ì™€ ì˜ˆì¸¡ê°’ì˜ ê°€ì¤‘ í‰ê· 
    const effectiveVolume = predictedVolume 
        ? currentVolume * 0.7 + predictedVolume * 0.3 
        : currentVolume;

    // ê¸°ë³¸ ë…¹ìƒ‰ ì‹œê°„ (5-30ì´ˆ)
    let greenTime = 5 + Math.round((effectiveVolume / 100) * 20);

    // í”¼í¬ ì‹œê°„ ë³´ì •
    if (isPeak) {
        greenTime = Math.min(greenTime * 1.2, 30);
    }

    // ìµœì†Œ/ìµœëŒ€ ì œí•œ
    return Math.max(5, Math.min(30, Math.round(greenTime)));
}

// ê° êµì°¨ë¡œë³„ë¡œ ì œì–´ ëª…ë ¹ ìƒì„±
for (let i = 0; i < 4; i++) {
    const ns_volume = volumes[i * 2];
    const ew_volume = volumes[i * 2 + 1];

    const ns_predicted = predictions ? predictions[i * 2] : ns_volume;
    const ew_predicted = predictions ? predictions[i * 2 + 1] : ew_volume;

    const ns_green_time = calculateOptimalGreenTime(ns_volume, ns_predicted, isPeakHour);
    const ew_green_time = calculateOptimalGreenTime(ew_volume, ew_predicted, isPeakHour);

    const congestionLevel = calculateCongestionLevel(ns_volume, ew_volume);
    const syncOffset = calculateSyncOffset(i);

    // PLCì— ì „ì†¡í•  ë°ì´í„° íŒ¨í‚· êµ¬ì„±
    // ì£¼ì†Œ 10-18: ì œì–´ íŒŒë¼ë¯¸í„°
    const controlData = [
        ns_green_time,     // ì£¼ì†Œ 10: NS ë…¹ìƒ‰ ì‹œê°„
        ew_green_time,     // ì£¼ì†Œ 11: EW ë…¹ìƒ‰ ì‹œê°„
        ns_volume,         // ì£¼ì†Œ 12: NS íŠ¸ë˜í”½ ë³¼ë¥¨
        ew_volume,         // ì£¼ì†Œ 13: EW íŠ¸ë˜í”½ ë³¼ë¥¨
        currentHour,       // ì£¼ì†Œ 14: í˜„ì¬ ì‹œê°„
        0,                 // ì£¼ì†Œ 15: NS ë³´í–‰ì ìš”ì²­ (PLCì—ì„œ ê´€ë¦¬)
        0,                 // ì£¼ì†Œ 16: EW ë³´í–‰ì ìš”ì²­ (PLCì—ì„œ ê´€ë¦¬)
        congestionLevel,   // ì£¼ì†Œ 17: í˜¼ì¡ë„
        syncOffset         // ì£¼ì†Œ 18: ë™ê¸°í™” ì˜¤í”„ì…‹
    ];

    // ê° êµì°¨ë¡œ PLCë¡œ ì „ì†¡
    node.send({
        topic: intersections[i],
        payload: {
            value: controlData,
            fc: 16,
            unitid: 1,
            address: 10,
            quantity: 9
        },
        diagnostics: {
            intersection: intersections[i].toUpperCase(),
            ns_volume: ns_volume,
            ew_volume: ew_volume,
            ns_green: ns_green_time,
            ew_green: ew_green_time,
            congestion: congestionLevel,
            syncOffset: syncOffset
        }
    });
}

return null;

// === í–¥ìƒëœ ë°ì´í„° ë¶„ë°°ê¸° ===

const topics = ['A_NS', 'A_EW', 'B_NS', 'B_EW', 'C_NS', 'C_EW', 'D_NS', 'D_EW'];
const outputs = [];

// í˜„ì¬ íŠ¸ë˜í”½ ë°ì´í„°
if (!Array.isArray(msg.payload) || msg.payload.length < 8) {
    for (let i = 0; i < 8; i++) {
        outputs.push({
            topic: topics[i],
            payload: 0,
            predicted: 0
        });
    }
    node.warn("Invalid traffic data, sending defaults");
    return outputs;
}

// í˜„ì¬ ë° ì˜ˆì¸¡ ë°ì´í„° ì²˜ë¦¬
for (let i = 0; i < 8; i++) {
    const current = msg.payload[i] || 0;
    const predicted = msg.payload[i + 8] || current;

    outputs.push({
        topic: topics[i],
        payload: current,
        predicted: predicted,
        trend: predicted > current ? 'up' : (predicted < current ? 'down' : 'stable')
    });
}

return outputs;

// === ê¸´ê¸‰ì°¨ëŸ‰ ìš°ì„  ì‹ í˜¸ ì œì–´ ===

const command = msg.payload;
const intersection = msg.intersection || 'a';
const direction = msg.direction || 1; // 1=NS, 2=EW

// ê¸´ê¸‰ì°¨ëŸ‰ ê²½ë¡œ ê³„ì‚° (ì—°ì†ëœ êµì°¨ë¡œ ì²˜ë¦¬)
const route = [];
if (command === 1) {
    // ê¸´ê¸‰ ëª¨ë“œ í™œì„±í™”
    const intersectionIndex = ['a', 'b', 'c', 'd'].indexOf(intersection);

    // í˜„ì¬ ë° ë‹¤ìŒ êµì°¨ë¡œì— ì‹ í˜¸ ì „ì†¡
    for (let i = intersectionIndex; i < Math.min(intersectionIndex + 2, 4); i++) {
        route.push({
            intersection: ['a', 'b', 'c', 'd'][i],
            delay: (i - intersectionIndex) * 10 // 10ì´ˆ ê°„ê²©
        });
    }
}

// ê° êµì°¨ë¡œì— EVP ì‹ í˜¸ ì „ì†¡
route.forEach((r, index) => {
    setTimeout(() => {
        node.send({
            payload: {
                value: [command, direction],
                fc: 16,
                unitid: 1,
                address: 20,
                quantity: 2
            },
            topic: r.intersection
        });
    }, r.delay * 1000);
});

if (command === 0) {
    // ëª¨ë“  êµì°¨ë¡œ EVP í•´ì œ
    ['a', 'b', 'c', 'd'].forEach(int => {
        node.send({
            payload: {
                value: [0, 0],
                fc: 16,
                unitid: 1,
                address: 20,
                quantity: 2
            },
            topic: int
        });
    });
}

return null;

// === ì‹œìŠ¤í…œ ìƒíƒœ ëª¨ë‹ˆí„°ë§ ===

const status = {
    timestamp: new Date().toISOString(),
    intersections: {
        a: { connected: true, mode: 'normal', cycles: 0 },
        b: { connected: true, mode: 'normal', cycles: 0 },
        c: { connected: true, mode: 'normal', cycles: 0 },
        d: { connected: true, mode: 'normal', cycles: 0 }
    },
    traffic: {
        avgVolume: 0,
        peakHour: false,
        congestionLevel: 'low'
    },
    performance: {
        avgWaitTime: 0,
        throughput: 0,
        efficiency: 0
    }
};

// ìƒíƒœ ì •ë³´ ìˆ˜ì§‘ ë° ê³„ì‚°
const volumes = msg.payload || [];
if (volumes.length >= 8) {
    const avgVolume = volumes.reduce((a, b) => a + b, 0) / volumes.length;
    status.traffic.avgVolume = Math.round(avgVolume);

    if (avgVolume > 70) {
        status.traffic.congestionLevel = 'high';
    } else if (avgVolume > 40) {
        status.traffic.congestionLevel = 'medium';
    }

    // íš¨ìœ¨ì„± ê³„ì‚° (ì‹œë®¬ë ˆì´ì…˜)
    status.performance.efficiency = Math.round(100 - avgVolume * 0.3);
    status.performance.avgWaitTime = Math.round(5 + avgVolume * 0.5);
    status.performance.throughput = Math.round(avgVolume * 12);
}

msg.payload = status;
return msg;

// msg.payloadëŠ” [NS_State, EW_State] í˜•ì‹ì˜ ë°°ì—´ì…ë‹ˆë‹¤.
// ê°€ì •: 1=ë…¹ìƒ‰, 2=í™©ìƒ‰, 3=ì ìƒ‰
const states = msg.payload;
if (!Array.isArray(states) || states.length < 2) {
    // ë°ì´í„°ê°€ ìœ íš¨í•˜ì§€ ì•Šìœ¼ë©´ ëª¨ë‘ ì ìƒ‰ìœ¼ë¡œ ê¸°ë³¸ê°’ ì„¤ì •
    msg.payload = { ns_color: 'red', ew_color: 'red' };
    return msg;
}

function mapStateToColor(state) {
    switch(state) {
        case 1: return 'lime';
        case 2: return 'yellow';
        case 3: return 'red';
        default: return '#555'; // êº¼ì§/íšŒìƒ‰
    }
}

msg.payload = {
    ns_color: mapStateToColor(states[0]),
    ew_color: mapStateToColor(states[1])
};

return msg;
