// === 지능형 트래픽 시뮬레이터 ===

const currentTime = new Date();
const hour = currentTime.getHours();
const minute = currentTime.getMinutes();
const dayOfWeek = currentTime.getDay();

// 시간대별 트래픽 패턴 정의
function getTrafficPattern(hour, isWeekend) {
    if (isWeekend) {
        // 주말 패턴
        if (hour >= 11 && hour <= 15) return { base: 60, variance: 20 };
        if (hour >= 18 && hour <= 21) return { base: 50, variance: 15 };
        return { base: 30, variance: 10 };
    } else {
        // 평일 패턴
        if (hour >= 7 && hour <= 9) return { base: 85, variance: 10 };   // 아침 러시
        if (hour >= 18 && hour <= 20) return { base: 90, variance: 10 }; // 저녁 러시
        if (hour >= 12 && hour <= 13) return { base: 60, variance: 15 }; // 점심시간
        if (hour >= 22 || hour <= 5) return { base: 15, variance: 5 };   // 심야
        return { base: 40, variance: 15 };
    }
}

// 교차로별 트래픽 특성 (위치별 가중치)
const intersectionWeights = [
    { ns: 1.0, ew: 0.9 },  // 교차로 A - 주도로/부도로
    { ns: 0.8, ew: 1.1 },  // 교차로 B - 부도로/상업지구
    { ns: 1.2, ew: 0.7 },  // 교차로 C - 대로/골목길
    { ns: 0.9, ew: 0.9 }   // 교차로 D - 균등
];

const isWeekend = dayOfWeek === 0 || dayOfWeek === 6;
const pattern = getTrafficPattern(hour, isWeekend);

// 각 교차로별 트래픽 볼륨 생성
const trafficVolumes = [];
for (let i = 0; i < 4; i++) {
    const weight = intersectionWeights[i];

    // 기본 트래픽에 변동성 추가
    let volumeNS = pattern.base * weight.ns;
    let volumeEW = pattern.base * weight.ew;

    // 랜덤 변동 추가 (±variance)
    volumeNS += (Math.random() - 0.5) * pattern.variance * 2;
    volumeEW += (Math.random() - 0.5) * pattern.variance * 2;

    // 특별 이벤트 시뮬레이션 (10% 확률로 한 방향 혼잡)
    if (Math.random() < 0.1) {
        if (Math.random() < 0.5) {
            volumeNS = Math.min(volumeNS * 1.5, 100);
        } else {
            volumeEW = Math.min(volumeEW * 1.5, 100);
        }
    }

    // 범위 제한 (0-100)
    volumeNS = Math.max(0, Math.min(100, Math.round(volumeNS)));
    volumeEW = Math.max(0, Math.min(100, Math.round(volumeEW)));

    trafficVolumes.push(volumeNS, volumeEW);
}

// TMC 허브에 데이터 쓰기
msg.payload = { 
    value: trafficVolumes, 
    fc: 16, 
    unitid: 1, 
    address: 100, 
    quantity: 8 
};

// 진단 정보 추가
msg.diagnostics = {
    timestamp: currentTime.toISOString(),
    hour: hour,
    isWeekend: isWeekend,
    pattern: pattern.base,
    volumes: trafficVolumes
};

return msg;

// === 교통량 예측 엔진 ===

// 이력 데이터 관리
let history = flow.get('trafficHistory') || [];
const MAX_HISTORY = 60; // 최근 5분 데이터 유지 (5초마다 샘플링)

// 현재 데이터 추가
if (msg.diagnostics && msg.diagnostics.volumes) {
    history.push({
        timestamp: Date.now(),
        volumes: msg.diagnostics.volumes,
        hour: msg.diagnostics.hour
    });

    // 최대 크기 유지
    if (history.length > MAX_HISTORY) {
        history = history.slice(-MAX_HISTORY);
    }

    flow.set('trafficHistory', history);
}

// 예측 수행
if (history.length >= 12) { // 최소 1분 데이터 필요
    const predictions = [];

    for (let i = 0; i < 8; i++) {
        // 각 차선별 이동 평균 계산
        const recentValues = history.slice(-12).map(h => h.volumes[i]);
        const average = recentValues.reduce((a, b) => a + b, 0) / recentValues.length;

        // 추세 계산
        const oldAvg = recentValues.slice(0, 6).reduce((a, b) => a + b, 0) / 6;
        const newAvg = recentValues.slice(6, 12).reduce((a, b) => a + b, 0) / 6;
        const trend = newAvg - oldAvg;

        // 예측값 = 평균 + 추세
        let prediction = average + trend * 0.5;
        prediction = Math.max(0, Math.min(100, Math.round(prediction)));

        predictions.push(prediction);
    }

    // 예측 데이터를 별도 주소에 저장
    msg.payload = {
        value: predictions,
        fc: 16,
        unitid: 1,
        address: 110, // 예측 데이터 주소
        quantity: 8
    };

    msg.predictions = predictions;
    return msg;
}

return null;

// === 향상된 적응형 제어 로직 ===

const volumes = msg.payload.slice(0, 8);      // 현재 트래픽
const predictions = msg.payload.slice(8, 16); // 예측 트래픽 (있으면)
const intersections = ['a', 'b', 'c', 'd'];

if (!Array.isArray(volumes) || volumes.length < 8) {
    node.warn("유효하지 않은 트래픽 데이터");
    return null;
}

const currentHour = new Date().getHours();
const isPeakHour = (currentHour >= 7 && currentHour <= 9) || 
                   (currentHour >= 18 && currentHour <= 20);

// 교차로 간 동기화를 위한 오프셋 계산
function calculateSyncOffset(index) {
    // 인접 교차로와 녹색 시작 시간을 조정하여 녹색파 생성
    const baseOffset = index * 15; // 15초씩 차이
    return baseOffset;
}

// 혼잡도 계산
function calculateCongestionLevel(volumeNS, volumeEW) {
    const maxVolume = Math.max(volumeNS, volumeEW);
    if (maxVolume > 85) return 2; // 높음
    if (maxVolume > 60) return 1; // 중간
    return 0; // 낮음
}

// 적응형 녹색 시간 계산
function calculateOptimalGreenTime(currentVolume, predictedVolume, isPeak) {
    // 현재와 예측값의 가중 평균
    const effectiveVolume = predictedVolume 
        ? currentVolume * 0.7 + predictedVolume * 0.3 
        : currentVolume;

    // 기본 녹색 시간 (5-30초)
    let greenTime = 5 + Math.round((effectiveVolume / 100) * 20);

    // 피크 시간 보정
    if (isPeak) {
        greenTime = Math.min(greenTime * 1.2, 30);
    }

    // 최소/최대 제한
    return Math.max(5, Math.min(30, Math.round(greenTime)));
}

// 각 교차로별로 제어 명령 생성
for (let i = 0; i < 4; i++) {
    const ns_volume = volumes[i * 2];
    const ew_volume = volumes[i * 2 + 1];

    const ns_predicted = predictions ? predictions[i * 2] : ns_volume;
    const ew_predicted = predictions ? predictions[i * 2 + 1] : ew_volume;

    const ns_green_time = calculateOptimalGreenTime(ns_volume, ns_predicted, isPeakHour);
    const ew_green_time = calculateOptimalGreenTime(ew_volume, ew_predicted, isPeakHour);

    const congestionLevel = calculateCongestionLevel(ns_volume, ew_volume);
    const syncOffset = calculateSyncOffset(i);

    // PLC에 전송할 데이터 패킷 구성
    // 주소 10-18: 제어 파라미터
    const controlData = [
        ns_green_time,     // 주소 10: NS 녹색 시간
        ew_green_time,     // 주소 11: EW 녹색 시간
        ns_volume,         // 주소 12: NS 트래픽 볼륨
        ew_volume,         // 주소 13: EW 트래픽 볼륨
        currentHour,       // 주소 14: 현재 시간
        0,                 // 주소 15: NS 보행자 요청 (PLC에서 관리)
        0,                 // 주소 16: EW 보행자 요청 (PLC에서 관리)
        congestionLevel,   // 주소 17: 혼잡도
        syncOffset         // 주소 18: 동기화 오프셋
    ];

    // 각 교차로 PLC로 전송
    node.send({
        topic: intersections[i],
        payload: {
            value: controlData,
            fc: 16,
            unitid: 1,
            address: 10,
            quantity: 9
        },
        diagnostics: {
            intersection: intersections[i].toUpperCase(),
            ns_volume: ns_volume,
            ew_volume: ew_volume,
            ns_green: ns_green_time,
            ew_green: ew_green_time,
            congestion: congestionLevel,
            syncOffset: syncOffset
        }
    });
}

return null;

// === 향상된 데이터 분배기 ===

const topics = ['A_NS', 'A_EW', 'B_NS', 'B_EW', 'C_NS', 'C_EW', 'D_NS', 'D_EW'];
const outputs = [];

// 현재 트래픽 데이터
if (!Array.isArray(msg.payload) || msg.payload.length < 8) {
    for (let i = 0; i < 8; i++) {
        outputs.push({
            topic: topics[i],
            payload: 0,
            predicted: 0
        });
    }
    node.warn("Invalid traffic data, sending defaults");
    return outputs;
}

// 현재 및 예측 데이터 처리
for (let i = 0; i < 8; i++) {
    const current = msg.payload[i] || 0;
    const predicted = msg.payload[i + 8] || current;

    outputs.push({
        topic: topics[i],
        payload: current,
        predicted: predicted,
        trend: predicted > current ? 'up' : (predicted < current ? 'down' : 'stable')
    });
}

return outputs;

// === 긴급차량 우선 신호 제어 (개별/전체 해제 기능 추가) ===

const payloadValue = msg.payload; // 버튼에서 온 값 (-1, 0, 1, 2)
const selectedIntersection = flow.get('selected_intersection') || 'a'; // 드롭다운에서 선택된 교차로

if (payloadValue === -1) {
    // === 개별 해제 로직 ===
    // 선택된 교차로 하나에만 해제 신호([0, 0])를 보냅니다.
    node.send({
        topic: selectedIntersection,
        payload: { value: [0, 0], fc: 16, unitid: 1, address: 20, quantity: 2 }
    });

} else if (payloadValue === 0) {
    // === 전체 해제 로직 ===
    // 모든 교차로에 해제 신호([0, 0])를 보냅니다.
    ['a', 'b', 'c', 'd'].forEach(int => {
        node.send({
            topic: int,
            payload: { value: [0, 0], fc: 16, unitid: 1, address: 20, quantity: 2 }
        });
    });

} else {
    // === 활성화 로직 (payload: 1 또는 2) ===
    // 선택된 교차로에 활성화 신호([1, 방향])를 보냅니다.
    node.send({
        topic: selectedIntersection,
        payload: {
            value: [1, payloadValue], // [활성화(1), 방향(1=NS, 2=EW)]
            fc: 16,
            unitid: 1,
            address: 20,
            quantity: 2
        }
    });
}

return null;

// === 시스템 상태 모니터링 (최종 수정 코드) ===

const status = {
    timestamp: new Date().toISOString(),
    traffic: {
        avgVolume: 0,
        congestionLevel: 'low'
    },
    performance: {
        avgWaitTime: 0,
        throughput: 0,
        efficiency: 0
    }
};

// msg.payload.data 에서 데이터 배열을 가져오도록 수정
const payloadData = msg.payload.data || [];

if (Array.isArray(payloadData) && payloadData.length >= 8) {

    const actualVolumes = payloadData.slice(0, 8);
    const avgVolume = actualVolumes.reduce((a, b) => a + b, 0) / actualVolumes.length;

    status.traffic.avgVolume = Math.round(avgVolume);

    if (avgVolume > 0) {
        if (avgVolume > 80) {
            status.traffic.congestionLevel = 'high';
        } else if (avgVolume > 50) {
            status.traffic.congestionLevel = 'medium';
        }

        status.performance.efficiency = Math.round(100 - avgVolume * 0.4);
        status.performance.avgWaitTime = Math.round(5 + avgVolume * 0.5);
        status.performance.throughput = Math.round(avgVolume * 15);
    }
}

msg.payload = status;
return msg;

let contextNS = flow.get('intersection_A_data_NS') || { wasGreen: false, startTime: Date.now() };
let contextEW = flow.get('intersection_A_data_EW') || { wasGreen: false, startTime: Date.now() };

const plcData = msg.payload;

if (!Array.isArray(plcData) || plcData.length < 12) {
