}

const l = plcData.map(val => val ? 1 : 0);

// South(l[2])/North(l[8]) are NS, East(l[5])/West(l[11]) are EW
const isGreenNS = (l[2] === 1 || l[8] === 1);
const isGreenEW = (l[5] === 1 || l[11] === 1);

