const lampMsg2 = { payload: { r: l[3], y: l[4], g: l[5] } }; // East
const lampMsg3 = { payload: { r: l[6], y: l[7], g: l[8] } }; // North
const lampMsg4 = { payload: { r: l[9], y: l[10], g: l[11] } }; // West

const timerMsgNS = { payload: calculatedTimerNS };
const timerMsgEW = { payload: calculatedTimerEW };

return [lampMsg1, lampMsg2, lampMsg3, lampMsg4, timerMsgNS, timerMsgEW];

